import React, { useEffect, useState } from 'react';
import './App.css';

export default function AdminReportPage() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [inboundQuestions, setInboundQuestions] = useState([]);
  const [inboundGrouped,    setInboundGrouped]    = useState([]);
  const [filter, setFilter] = useState('All');

  useEffect(() => {
    fetch('http://localhost:3001/all-submissions')
      .then((res) => res.json())
      .then((json) => {
        setData(json);
        setFilteredData(json);
      });

    fetch('http://localhost:3001/inbound-submissions')
      .then((res) => res.json())
      .then((json) => setInboundQuestions(json));

      fetch('http://localhost:3001/inbound-submissions-grouped')
     .then((res) => res.json())
     .then((json) => setInboundGrouped(json));
  }, []);

  useEffect(() => {
    if (filter === 'All') {
      setFilteredData(data);
    } else {
      setFilteredData(data.filter((item) => item.flow_type === filter));
    }
  }, [filter, data]);

  return (
    <div className="admin-container">
      <h2 className="admin-title">ðŸ“Š Admin Report Dashboard</h2>

      <div className="filter-buttons">
        <button onClick={() => setFilter('All')}>All</button>
        <button onClick={() => setFilter('Inbound')}>Inbound</button>
        <button onClick={() => setFilter('Outbound')}>Outbound</button>
      </div>

      {/* First Table: Confirmations (Data Elements) */}
      <div className="admin-table-container">
        <h3>Data Element Submissions</h3>
        <table className="admin-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>System</th>
              <th>Module</th>
              <th>Data Element</th>
              <th>Confirmed</th>
              <th>Remarks</th>
              <th>Timestamp</th>
              <th>Flow Type</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.length > 0 ? (
              filteredData.map((row, idx) => (
                <tr key={idx}>
                  <td>{row.id}</td>
                  <td>{row.system_name}</td>
                  <td>{row.module_name}</td>
                  <td>{row.data_element}</td>
                  <td>{row.is_confirmed === 1 ? 'Yes' : 'No'}</td>
                  <td>{row.remarks}</td>
                  <td>{row.created_at}</td>
                  <td>{row.flow_type}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" style={{ textAlign: 'center' }}>No data available.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Second Table: Inbound Questions */}
      {(filter === 'All' || filter === 'Inbound') && (
        <div className="admin-table-container">
          <h3>Inbound Requirement Questions</h3>
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>System</th>
                <th>Module</th>
                <th>Question ID</th>
                <th>Question</th>
                <th>Answer</th>
                <th>File (if any)</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {inboundQuestions.length > 0 ? (
                inboundQuestions.map((row, idx) => (
                  <tr key={idx}>
                    <td>{row.id}</td>
                    <td>{row.system_name}</td>
                    <td>{row.module_name}</td>
                    <td>{row.question_id}</td>
                    <td>{row.question_text}</td>
 <td>
  {/* Main answer value */}
  <pre style={{ whiteSpace: 'pre-wrap' }}>{row.answer}</pre>

  {/* Conditionally show grid table */}
  {row.question_id === 'dataInvolved' && row.gridData?.length > 0 && (
    <div style={{ marginTop: '10px' }}>
      <table className="mini-grid">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Jenis</th>
            <th>Saiz</th>
            <th>Nullable</th>
            <th>Rules</th>
          </tr>
        </thead>
        <tbody>
          {row.gridData.map((gridRow, index) => (
            <tr key={index}>
              <td>{gridRow.nama}</td>
              <td>{gridRow.jenis}</td>
              <td>{gridRow.saiz}</td>
              <td>{gridRow.nullable}</td>
              <td>{gridRow.rules}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )}
</td>



                    <td>
                      {row.file_path ? (
                        <a href={`http://localhost:3001/${row.file_path}`} target="_blank" rel="noreferrer">Download</a>
                      ) : (
                        'â€”'
                      )}
                    </td>
                    <td>{row.created_at}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="8" style={{ textAlign: 'center' }}>No inbound questions submitted.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
            {/* Third Table: Inbound Data Grid */}
      {(filter === 'All' || filter === 'Inbound') && (
        <div className="admin-table-container">
          <h3>Inbound Data Grid Rows</h3>

          {inboundGrouped.length > 0 ? (
            inboundGrouped.map((sub, sIdx) => (
              <div key={sIdx} style={{ marginBottom: '25px' }}>
                {/* Header per submission */}
                <div style={{ fontWeight: 600, marginBottom: 6 }}>
                  {sub.system} â€“ {sub.module} (Timestamp: {sub.created_at})
                </div>

                <table className="admin-table mini-grid">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Nama</th>
                      <th>Jenis</th>
                      <th>Saiz</th>
                      <th>Nullable</th>
                      <th>Rules</th>
                    </tr>
                  </thead>
                  <tbody>
                    {sub.gridData.length > 0 ? (
                      sub.gridData.map((g, gIdx) => (
                        <tr key={gIdx}>
                          <td>{gIdx + 1}</td>
                          <td>{g.nama}</td>
                          <td>{g.jenis}</td>
                          <td>{g.saiz}</td>
                          <td>{g.nullable}</td>
                          <td>{g.rules}</td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="6" style={{ textAlign: 'center' }}>
                          No grid rows submitted.
                        </td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            ))
          ) : (
            <p>No inbound grid data available.</p>
          )}
        </div>
      )}

    </div>
  );
}
