import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import systemsData from './systems.json'; // adjust path if needed

export default function InboundRequirementForm() {
  const [formData, setFormData] = useState({});
  const [files, setFiles] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [modules, setModules] = useState([]);

useEffect(() => {
  const systemName = "Sistem Pengurusan Meja Bantuan (SPMB)";
  const inboundModules = systemsData?.Inbound?.[systemName] || {};
  const moduleNames = Object.keys(inboundModules);

  setFormData(prev => ({ ...prev, system: systemName }));
  setModules(moduleNames);
}, []);



  const questions = [
    {
      id: 'q1',
      text: '1. Apakah data yang akan dihantar atau diterima?',
      tooltip: 'Sediakan senarai medan, format (cth: string, date), dan sama ada wajib atau tidak.',
      upload: true
    },
    {
      id: 'q2',
      text: '2. Seberapa kerap data dikemas kini atau diperlukan?',
      tooltip: 'Nyatakan kekerapan (real-time, harian, mingguan) dan format (CSV, JSON).'
    },
    {
      id: 'q3',
      text: '3. Apakah kaedah integrasi yang disokong?',
      tooltip: 'REST API, MYGDX, SFTP? Nyatakan kaedah, endpoint dan kaedah auth jika ada.'
    },
    {
      id: 'q4',
      text: '4. Adakah anda mempunyai dokumentasi API atau WSDL?',
      tooltip: 'Lampirkan Swagger, Postman collection, atau dokumentasi API yang tersedia.',
      upload: true
    },
    {
      id: 'q5',
      text: '5. Apakah logik semakan atau perniagaan sistem anda?',
      tooltip: 'Contoh: Kod jawatan wajib wujud, status aktif, dll.'
    },
    {
      id: 'q6',
      text: '6. Siapakah pegawai teknikal (PIC) untuk ujian integrasi?',
      tooltip: 'Nyatakan nama, nombor telefon dan email PIC.'
    },
    {
      id: 'q7',
      text: '7. Adakah terdapat sekatan firewall/IP?',
      tooltip: 'Senaraikan IP agensi yang perlu dibuka akses jika ada.'
    },
    {
      id: 'q8',
      text: '8. Adakah persekitaran UAT tersedia untuk ujian?',
      tooltip: 'Berikan URL UAT, kelayakan login dan sebarang data dummy jika ada.'
    },
    {
      id: 'q9',
      text: '9. Apakah masa respons yang dijangka (SLA)?',
      tooltip: 'Contoh: 2 saat purata. Sila nyatakan retry/timeout jika berkaitan.'
    },
    {
      id: 'q10',
      text: '10. Adakah anda memerlukan log audit, mesej ralat, atau callback?',
      tooltip: 'Sila nyatakan keperluan audit trail, mekanisme callback atau pengurusan error.',
      upload: true
    }
  ];

  const handleChange = (id, value) => {
    setFormData(prev => ({ ...prev, [id]: value }));
  };

  const handleFileChange = (id, file) => {
    setFiles(prev => ({ ...prev, [id]: file }));
  };

  const handleSubmit = async () => {
    const form = new FormData();
    Object.entries(formData).forEach(([key, val]) => form.append(key, val));
    Object.entries(files).forEach(([key, file]) => form.append(key, file));

    setIsSubmitting(true);
    try {
      await axios.post('http://localhost:3001/submit-inbound', form, {

        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert("Successfully submitted requirement gathering form.");
      setFormData({});
      setFiles({});
    } catch (err) {
      console.error(err);
      alert("Submission failed. Check console.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container">
      <h2 className="inbound-title">📋 Requirement Gathering (Inbound)</h2>
{/* System Name Field */}
<div className="question-block">
  <label className="question-label">System Name</label>
  <input
    type="text"
    value="Sistem Pengurusan Meja Bantuan (SPMB)"
    readOnly
  />
</div>


{/* Module Name Field */}
<div className="question-block">
  <label className="question-label">Module Name</label>
  <select
    value={formData.module || ''}
    onChange={(e) => handleChange('module', e.target.value)}
  >
    <option value="">-- Pilih Modul --</option>
    {modules.map((mod, idx) => (
      <option key={idx} value={mod}>{mod}</option>
    ))}
  </select>
</div>

{/* Loop for the 10 Questions */}

      {questions.map((q) => (
        <div key={q.id} className="question-block">
          <label className="question-label">
            {q.text}
            <span className="info-icon" title={q.tooltip}>i</span>
          </label>
          <textarea
            rows={4}
            value={formData[q.id] || ''}
            onChange={(e) => handleChange(q.id, e.target.value)}
            placeholder="Sila jawab di sini..."
          />
          {q.upload && (
            <div style={{ marginTop: '10px' }}>
              <label>Muat naik fail sokongan (PDF/DOCX):</label><br />
              <input
                type="file"
                accept=".pdf,.doc,.docx"
                onChange={(e) => handleFileChange(q.id, e.target.files[0])}


              />
            </div>
          )}
        </div>
      ))}

      <div className="button-group" style={{ marginTop: '30px' }}>
        <button onClick={handleSubmit} disabled={isSubmitting}>Hantar</button>
      </div>
    </div>
  );
}
